import React, { Component } from "react";

class Reservation extends Component {
  constructor(props) {
    super(props);

    this.state = {
      flights: [{orderId: "1", name: "Joghn Doe" ,  fromLoc: "SFO",
      toLoc: "LAX",
      departDate: "12/19/20021", price: "342"}],

    //   orderId: "",
    //   name: "",  
    //   fromLoc: "",
    //   toLoc: "",
    //   departDate: "", 
    //   price: 0
    };
  }

  flightList() {
    fetch("http://localhost:3001")
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response.json().then((data) => {
          this.setState({ flights: data});
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  componentDidMount() {
    this.flightList();
  }


  cancelClick(orderId) {
    if (window.confirm("Are you sure you want to cancel?")) {
      fetch("http://localhost:3001/" + orderId, {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      })
        .then((res) => res.json())
        .then(
          (result) => {
            alert(result);
            this.flightList();
          },
          (error) => {
            alert("Failed");
          }
        );
    }
  }

  render() {

    const { flights, orderId, name,  fromLoc, toLoc, departDate, price} = this.state;

    return (
      <div>
        <h1 className="headerh1"> Reservation</h1>

        <table className="table table-striped">
          <thead>
            <tr>
              <th>Order#</th>
              <th>Name</th>

              <th>Depart from</th>

              <th>Destination</th>
              <th>Date</th>
              <th>Price</th>
            </tr>
          </thead>

          <tbody>
            {flights.map((x) => (
              <tr key={x.orderId}>
                <td>{x.orderId}</td>
                <td>{x.name}</td>
                <td>{x.fromLoc}</td>
                <td>{x.toLoc}</td>
                <td>{x.departDate}</td>
                <td>${x.price}</td>
                <td>
                  <button
                    type="button"
                    className="btn btn-light mr-1"
                    onClick={() => this.cancelClick(x.orderId)}
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="16"
                      height="16"
                      fill="currentColor"
                      class="bi bi-cart"
                      viewBox="0 0 16 16"
                    >
                      <path d="M0 1.5A.5.5 0 0 1 .5 1H2a.5.5 0 0 1 .485.379L2.89 3H14.5a.5.5 0 0 1 .491.592l-1.5 8A.5.5 0 0 1 13 12H4a.5.5 0 0 1-.491-.408L2.01 3.607 1.61 2H.5a.5.5 0 0 1-.5-.5zM3.102 4l1.313 7h8.17l1.313-7H3.102zM5 12a2 2 0 1 0 0 4 2 2 0 0 0 0-4zm7 0a2 2 0 1 0 0 4 2 2 0 0 0 0-4zm-7 1a1 1 0 1 1 0 2 1 1 0 0 1 0-2zm7 0a1 1 0 1 1 0 2 1 1 0 0 1 0-2z" />
                    </svg>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
export default Reservation;
