import React, { Component } from "react";

class HomeBackground extends Component {
  render() {
    return (
      <div
        style={{
          backgroundImage: `url("http://localhost:3000/images/backgroundHome.jpg")`,
          backgroundRepeat: "no-repeat",
          width: "100",
          height: "150px",
          backgroundSize: "cover",
          position: "relative",
          opacity: "0.9",
        }}
      ></div>
    );
  }
}
export default HomeBackground;

class ReactSelectExample extends Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
        selectedOption: {}
    }
  }

fetchData = (inputValue, callback) => {
    if (!inputValue) {
      callback([]);
    } else {
        setTimeout(() => {
  fetch("http://localhost:31962/api/values/findusers/" + inputValue, {
    method: "GET",
  })
  .then((resp) => {
    return resp.json()
  }) 
  .then((data) => {
      const tempArray = [];
     data.forEach((element) => {
            tempArray.push({ label: `${element.name}`, value: element.email });
     });
     callback(tempArray);            
  })
  .catch((error) => {
    console.log(error, "catch the hoop")
  });
});
}
}

 onSearchChange = (selectedOption) => {
    if (selectedOption) {

    this.setState({
        selectedOption
       });
    }
  };
  render() {
      return ( <div>
           <AsyncSelect
                value={this.state.selectedOption}
                loadOptions={this.fetchData}
                placeholder="Admin Name"
                className="venueAdminControl"
                onChange={(e) => {
                    this.onSearchChange(e);
                }}
                defaultOptions={false}
                />
      </div>)
  }

}
