import React, { Component } from "react";
import Select from 'react-select';
import AsyncSelect from 'react-async-select';

class Flight extends Component {
    constructor(props) {
        super(props);

        this.state = {
            flights: [],
          fromLoc: "",
          toLoc: "",
          departTime: "",
          arrivetTime: "",
          name: "",
          passenger: 0,
          flights: [],
          error: null
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
      }

      handleChange(event) {
        this.setState({ searchValue: event.target.value });
        fetch("http://localhost:3001")
          .then((response) => {
            if (!response.ok) {
              throw new Error(response.statusText);
            }
            return response.json().then((data) => {

              this.setState({ flights: data });
              this.setState({ fromLoc: data.fromLoc });
              this.setState({ toLoc: data.toLoc });
              this.setState({ departTime: data.departTime });
              this.setState({ arrivetTime: data.arrivetTime });
              this.setState({ passenger: data.passenger });
            });
          })
          .catch((err) => {
            console.log(err);
          });

      }

      handleSearch(event) {
        event.preventDefault();
        console.log(this.state.searchValue);
        // console.log(typeof(this.state.searchValue));
        console.log(this.state.flights);
        let x = false;
        for (let i = 0; i < this.state.runners.length; i++) {
          // console.log(this.state.runners[i].bib_number);
          // console.log(this.state.searchValue);

          if ((this.state.fromLoc == this.state.searchValue1) && (this.state.toLoc == this.state.searchValue2) && (this.state.departTime == this.state.searchValue3)){
            console.log("working");
            x = true;
          } else console.log("flag is false");
        }

        console.log(x);

       x == true ? (


          fetch(`http://localhost:3001}`, {
            method: "GET",
          })
            .then((response) => {
              return response.json();
            })
            .then((data) => {
              console.log(data.map((x) => x.flights));
              console.log(data);
              this.setState({ flights: data });
              this.setState({error: null});


            })


            ) : this.setState({ error: "Please enter all fields." })
            console.log(this.state.error)
      }

      errorHandle() {
        return (
          <div style={{ color:"#fff", textAlign:"center" }}>{ <h1>error</h1> }</div>
        )
      }

      render() {
        const { error } = this.state;

        return (
          <>
            <div>
              <div>
                <h3 style={{ fontFamily: "Copperplate" }}>
                  {" "}
                  Search for flights
                </h3>

                <form className="form">
                  {/* <label> Bib number </label> */}
                  <input
                    type="text"
                    placeholder="From"
                    value={this.state.searchValue1}
                    onChange={this.handleChange}
                  />
                    <input
                    label = "from"
                    type="text"
                    placeholder="To"
                    value={this.state.searchValue2}
                    onChange={this.handleChange}
                  />
                    <input
                    type="text"
                    placeholder="Date"
                    value={this.state.searchValue3}
                    onChange={this.handleChange}
                  />
                </form>
                <button
                  value="Submit"
                  className="button"
                  onClick={this.handleSearch}
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="30"
                    height="16"
                    fill="currentColor"
                    className="bi bi-search"
                    viewBox="0 0 16 16"
                  >
                    <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
                  </svg>
                  Search
                </button>
              </div>
            </div>

            <div className="center">
              {/* // images render  */}

              {/* //</div> <div className="card-list">  */}

              <div className="row">
                {this.state.error !== null ? (
                  <div
                    style={{
                      color: "#000000",
                      textAlign: "center",
                      fontFamily: "Copperplate",
                      fontSize: "25px",
                    }}
                  >
                    {error}
                  </div>
                ) : (
                  this.state.img_name.map((link, index) => (
                    <div className="column" key={index} accept=".png">
                      <img
                        className="single-img"
                        src={`http://localhost:3000/images/${link.name}`}
                        alt="img-thumbnail"
                        width="100%"
                      />
                    </div>
                  ))
                )}
              </div>
            </div>
          </>
        );
      }
    }

export default Flight;
