import React, { Component } from "react";
import {Navigate} from 'react-router-dom';
import BibDisplay from "../BibDisplay/BibDisplay";
import Select from 'react-select';
import AsyncSelect from 'react-async-select';

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchOption: "",
      runners: [],
      eventName: [],
      name: "",
      bib_number: 0,



      aquaticCreatures: [
        { label: "Shark" },
        { label: "Dolphin", value: "Dolphinsss" },
        { label: "Whale", value: "Whale" },
        { label: "Octopus", value: "Octopus" },
        { label: "Crab", value: "Crab" },
        { label: "Lobster", value: "Lobster" },
      ],
    };


    this.handleSearch = this.handleSearch.bind();
  }

  handleSearch() {

    fetch("http://localhost:3001")
      .then((response) => {
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return response.json().then((data) => {

          console.log(data)
          const temp = data.map(x => ({
            value : x.event_name,
          label : x.bib_number}))
          this.setState({eventName: temp})
        });
      })
      .catch((err) => {
        console.log(err);
      });
      console.log(this.eventName)
  }

  componentDidMount(){
    this.handleSearch()
}


  render() {
    const { runners, searchOption, eventName, aquaticCreatures } =
      this.state;

    return (
      <>
        <div className="container-fluid">
          <div>
            <h3> Enter Event</h3>

            <form className="form">
              {/* <label> Bib number </label> */}
              <input
                type="text"
                placeholder="Search event name"
                value={this.state.searchOption}
                onChange={this.handleChange}
              />
            </form>
            <button
              value="Submit"
              className="button"
              onClick={this.handleSearch}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="30"
                height="16"
                fill="currentColor"
                className="bi bi-search"
                viewBox="0 0 16 16"
              >
                <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
              </svg>
              Search
            </button>
          </div>

          <div className="selectContainer">
            <Select
              options={eventName}
              onChange={opt => console.log(opt.label, opt.value)}
              // onChange={(opt) => window.location.assign("/bib")}
              defaultOptions={false}
            />
          </div>



        </div>
      </>
    );
  }
}
export default Home;









